import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function SearchFilters({ onSearch }) {
  const [searchCriteria, setSearchCriteria] = useState({
    city: '',
    state: '',
    country: '',
    startDate: null,
    endDate: null,
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSearchCriteria({ ...searchCriteria, [name]: value });
  };

  const handleStartDateChange = (date) => {
    setSearchCriteria({ ...searchCriteria, startDate: date });
  };

  const handleEndDateChange = (date) => {
    setSearchCriteria({ ...searchCriteria, endDate: date });
  };

  const handleSearch = () => {
    // Handle the case where endDate is null
    const updatedCriteria = {
      ...searchCriteria,
      endDate: searchCriteria.endDate === null ? 'indefinite' : searchCriteria.endDate,
    };

    onSearch(updatedCriteria);
  };

  return (
    <div>
      <h2>Search Filters</h2>
      <div>
        <label htmlFor="city">City:</label>
        <select name="city" id="city" onChange={handleInputChange} value={searchCriteria.city}>
        <option value="">Select City</option>
          <option value="Atlanta">Atlanta</option>
          <option value="Albuquerque">Albuquerque</option>
          <option value="Los Angeles">Los Angeles</option>
        </select>
      </div>
      <div>
        <label htmlFor="state">State:</label>
        <select name="state" id="state" onChange={handleInputChange} value={searchCriteria.state}>
        <option value="">Select State</option>
          <option value="California">California</option>
          <option value="Georgia">Georgia</option>
          <option value="New Mexico">New Mexico</option>
        </select>
      </div>
      <div>
        <label htmlFor="country">Country:</label>
        <select name="country" id="country" onChange={handleInputChange} value={searchCriteria.country}>
        <option value="">Select Country</option>
        <option value="United States of America">United States of America</option>
        </select>
      </div>
      <div>
        <label>Start Date:</label>
        <DatePicker selected={searchCriteria.startDate} onChange={handleStartDateChange} />
      </div>
      <div>
        <label>End Date:</label>
        <DatePicker selected={searchCriteria.endDate} onChange={handleEndDateChange} />
      </div>
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchFilters;
