import React from 'react';

function SoundStageList({ soundStages }) {
  // Check if soundStages is defined and is an array
  if (!soundStages || !Array.isArray(soundStages)) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Sound Stages</h2>
      {soundStages.map((studio, index) => (
        <div key={index} className="card mb-4">
          <div className="card-body">
            <h3 className="card-title">{studio.studio_name}</h3>
            {studio.sound_stages.map((soundStage, stageIndex) => (
              <div key={stageIndex}>
                <h4 className="card-subtitle mb-2">Sound Stage {soundStage.stage_number}</h4>
                <p className="mb-1"><strong>City:</strong> {studio.city}</p>
                <p className="mb-1"><strong>State:</strong> {studio.state}</p>
                <p className="mb-1"><strong>Country:</strong> {studio.country}</p>
                {/* Display available_dates */}
                <h5 className="mt-3">Available Dates:</h5>
                <ul className="list-unstyled">
                  {soundStage.available_dates.map((date, dateIndex) => (
                    <li key={dateIndex}>
                      <strong>Start Date:</strong> {date.start_date}
                      {date.end_date && <span>, <strong>End Date:</strong> {date.end_date}</span>}
                    </li>
                  ))}
                </ul>
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}

export default SoundStageList;
