import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';

function SearchFilters({ onSearch }) {
  const [searchCriteria, setSearchCriteria] = useState({
    city: '',
    state: '',
    country: '',
    startDate: null, // Add startDate state
    endDate: null,   // Add endDate state
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setSearchCriteria({ ...searchCriteria, [name]: value });
  };

  const handleStartDateChange = (date) => {
    setSearchCriteria({ ...searchCriteria, startDate: date });
  };

  const handleEndDateChange = (date) => {
    setSearchCriteria({ ...searchCriteria, endDate: date });
  };

  const handleSearch = () => {
    onSearch(searchCriteria);
  };

  return (
    <div>
      <h2>Search Filters</h2>
      <div>
        <label htmlFor="city">City:</label>
        <select name="city" id="city" onChange={handleInputChange} value={searchCriteria.city}>
          {/* ... City options */}
        </select>
      </div>
      <div>
        <label htmlFor="state">State:</label>
        <select name="state" id="state" onChange={handleInputChange} value={searchCriteria.state}>
          {/* ... State options */}
        </select>
      </div>
      <div>
        <label htmlFor="country">Country:</label>
        <select name="country" id="country" onChange={handleInputChange} value={searchCriteria.country}>
          {/* ... Country options */}
        </select>
      </div>
      <div>
        <label>Start Date:</label>
        <DatePicker selected={searchCriteria.startDate} onChange={handleStartDateChange} />
      </div>
      <div>
        <label>End Date:</label>
        <DatePicker selected={searchCriteria.endDate} onChange={handleEndDateChange} />
      </div>
      <button onClick={handleSearch}>Search</button>
    </div>
  );
}

export default SearchFilters;
